css:
/* General Layout */
body {
  font-family: 'Segoe UI', Roboto, sans-serif;
  background-color: #f9f9f9;
  color: #333;
  padding: 30px;
  max-width: 700px;
  margin: auto;
  box-shadow: 0 0 10px rgba(0,0,0,0.05);
  border-radius: 8px;
}

div[style*="text-align: center"] h2 {
  font-size: 24px;
  margin-top: 10px;
  color: #2c3e50;
  letter-spacing: 1px;
}

img[alt="Airport Logo"] {
  border-radius: 6px;
  box-shadow: 0 2px 6px rgba(0,0,0,0.1);
}

table {
  width: 100%;
  border-collapse: collapse;
  background-color: #fff;
  border: 1px solid #ddd;
  border-radius: 6px;
  overflow: hidden;
}

table tr {
  border-bottom: 1px solid #eee;
}

table td {
  padding: 12px 16px;
  vertical-align: top;
}

table td:first-child {
  font-weight: 600;
  color: #555;
  width: 40%;
  background-color: #f4f6f8;
}

table td:last-child {
  color: #2c3e50;
}

/* Footer Note */
div[style*="margin-top: 30px"] p {
  font-style: italic;
  color: #27ae60;
  font-size: 16px;
  margin-top: 20px;
}
